package Java_Break;

public class Break_while {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    //while loop  
	    int i=1;  
	    while(i<=10){  
	        if(i==5){  
	            //using break statement  
	            i++;  
	            break;//it will break the loop  
	        }  
	        System.out.println(i);  
	        i++;  
	    } 
	}

}
